sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function setOperatingSystem({ api, event, helpers, imports }) {
  // Setzt den Client State Parameter auf "Windows"
  api.setState('userBetriebssystem', 'Apple');
}
